mapper.select:
SELECT G.GNR_ID , G.GNR_NM , TO_CHAR(G.CRT_DT,'YYYY-MM-DD HH24:MI:SS') CRT_DT , G.CRTR , TO_CHAR(G.MDFY_DT,'YYYY-MM-DD HH24:MI:SS') MDFY_DT , G.MDFYR , G.USE_YN , G.DEL_YN , CM.MBR_NM CRTR_NM , MM.MBR_NM MDFYR_NM FROM GNR G JOIN MBR CM ON CM.MBR_ID = G.CRTR JOIN MBR MM ON MM.MBR_ID = G.MDFYR WHERE G.DEL_YN = 'N'
AND GNR_NM = #{_parameter}
ORDER BY G.GNR_ID

mapper.update:
UPDATE GNR
   SET GNR_NM=#{gnrNm}
   	 , MDFY_DT=SYSDATE
   	 , MDFYR=#{mdfyr}
   	 , USE_YN=NVL(#{useYn},'N')
 WHERE GNR_ID=#{gnrId}

mapper.delete:
UPDATE GNR
   SET DEL_YN = 'Y'
 WHERE GNR_ID=#{_parameter}
